{
  "name": "food-delivery-table-reservation",
  "version": "1.0.0",
  "private": true,
  "scripts": {
    "dev": "vite --host 0.0.0.0 --port 5173",
    "build": "vite build",
    "preview": "vite preview --host 0.0.0.0 --port 5173"
  },
  "dependencies": {
    "react": "^18.2.0",
    "react-dom": "^18.2.0",
    "react-router-dom": "^6.14.0",
    "axios": "^1.3.0"
  },
  "devDependencies": {
    "vite": "^4.5.0",
    "@vitejs/plugin-react": "^3.1.0",
    "typescript": "^5.0.0",
    "tailwindcss": "^3.4.0",
    "postcss": "^8.4.0",
    "autoprefixer": "^10.4.0",
    "eslint": "^8.30.0",
    "eslint-plugin-react": "^7.32.0",
    "prettier": "^2.8.0"
  },
  "eslintConfig": {
    "extends": [
      "plugin:react/recommended",
      "plugin:prettier/recommended"
    ],
    "rules": {
      "react/react-in-jsx-scope": "off"
    }
  },
  "prettier": {
    "singleQuote": true,
    "trailingComma": "all"
  },
  "browserslist": [
    ">0.2%",
    "not dead",
    "not op_mini all"
  ]
}

import { defineConfig } from 'vite';
import react from '@vitejs/plugin-react';
import path from 'path';

export default defineConfig({
  plugins: [react()],
  server: { host: '0.0.0.0', port: 5173 },
  resolve: { alias: { '@': path.resolve(__dirname, './src') } },
  build: { outDir: 'dist', assetsDir: 'assets' }
});

/** @type {import('tailwindcss').Config} */
module.exports = {
  content: ['./index.html', './src/**/*.{js,jsx,ts,tsx}'],
  safelist: [], // will be generated from slots.style.allowedClasses
  theme: {
    extend: {
      colors: {
        primary: '#FF6347',
        secondary: '#4CAF50',
        accent: '#FFC107',
        background: '#F9F9F9',
        text: { light: '#FFFFFF', dark: '#333333' }
      }
    }
  },
  plugins: [],
  darkMode: 'class'
};

import React from 'react';
import ReactDOM from 'react-dom/client';
import { BrowserRouter } from 'react-router-dom';
import App from './App';
import './index.css';

const rootElement = document.getElementById('root');
if (!rootElement) throw new Error('Failed to find the root element');

const root = ReactDOM.createRoot(rootElement);
root.render(
  <React.StrictMode>
    <BrowserRouter>
      <App />
    </BrowserRouter>
  </React.StrictMode>
);

import React from 'react';
import { Routes, Route } from 'react-router-dom';
import Header from './components/Header';
import Footer from './components/Footer';
import Home from './pages/Home';
import Menu from './pages/Menu';
import Reservation from './pages/Reservation';
import ChefProfile from './pages/ChefProfile';
import DishGallery from './pages/DishGallery';

const App: React.FC = () => {
  return (
    <div className="min-h-screen flex flex-col bg-orange-50">
      <Header restaurantName="{{header.restaurantName}}" tagline="{{header.tagline}}" />
      <main className="flex-1">
        <Routes>
          <Route path="/" element={<Home />} />
          <Route path="/menu" element={<Menu />} />
          <Route path="/reservation" element={<Reservation availableSlots={{reservation.availableSlots}} />} />
          <Route path="/chef-profile" element={<ChefProfile name="{{chef.name}}" photo="{{chef.photo}}" biography="{{chef.biography}}" specialties={{chef.specialties}} philosophy="{{chef.philosophy}}" awards={{chef.awards}} signatureDishes={{chef.signatureDishes}} />} />
          <Route path="/dish-gallery" element={<DishGallery />} />
        </Routes>
      </main>
      <Footer restaurantName="{{footer.restaurantName}}" address="{{footer.address}}" phone="{{footer.phone}}" socialLinks={{footer.socialLinks}} />
    </div>
  );
};

export default App;

@tailwind base;
@tailwind components;
@tailwind utilities;

@layer base {
  html { font-size: 16px; line-height: 1.5; color: #3a3a3a; background-color: #fffaf0; }
  body { font-family: 'Inter', sans-serif; margin: 0; padding: 0; }
  h1, h2, h3, h4, h5, h6 { margin: 0; font-weight: 600; }
  p { margin: 0; }
}

@layer components {
  .btn { @apply px-4 py-2 rounded-lg text-white bg-orange-600 hover:bg-orange-500 transition duration-300 ease-in-out; }
  .card { @apply bg-white shadow-md rounded-lg p-6 mb-4; }
  .header { @apply bg-orange-600 text-white p-4; }
  .footer { @apply bg-gray-200 text-gray-700 p-4 text-center; }
}

import React, { useState } from 'react';
import { Link } from 'react-router-dom';

interface MenuItem { id: number; name: string; description: string; price: string; imageUrl: string; dietaryInfo: string[]; }

const menuItems: MenuItem[] = {{menu.items}};

const Menu: React.FC = () => {
  const [searchTerm, setSearchTerm] = useState('');
  const filteredItems = menuItems.filter(item => item.name.toLowerCase().includes(searchTerm.toLowerCase()));

  return (
    <div className="min-h-screen p-8 bg-gray-100">
      <div className="max-w-4xl mx-auto">
        <h1 className="text-4xl font-bold mb-6 text-center">{{menu.title}}</h1>
        <input type="text" placeholder="Search menu items..." className="mb-6 p-2 border border-gray-300 rounded w-full" value={searchTerm} onChange={e => setSearchTerm(e.target.value)} />
        <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-3 gap-6">
          {filteredItems.map(item => (
            <div key={item.id} className="bg-white p-4 rounded shadow-md">
              <img src={item.imageUrl} alt={item.name} className="w-full h-48 object-cover rounded" />
              <h2 className="text-xl font-semibold mt-4">{item.name}</h2>
              <p className="text-gray-600">{item.description}</p>
              <p className="font-bold text-lg mt-2">{item.price}</p>
              <p className="text-sm text-gray-500 mt-1">{item.dietaryInfo.length > 0 ? `Dietary: ${item.dietaryInfo.join(', ')}` : '—'}</p>
            </div>
          ))}
        </div>
        <Link to="/" className="block mt-8 text-blue-500 hover:underline text-center">Back to Home</Link>
      </div>
    </div>
  );
};

export default Menu;

import React, { useState } from 'react';
import { Link } from 'react-router-dom';

interface ReservationFormProps { availableSlots: string[]; }

const Reservation: React.FC<ReservationFormProps> = ({ availableSlots }) => {
  const [date, setDate] = useState('');
  const [time, setTime] = useState('');
  const [guests, setGuests] = useState(1);
  const [table, setTable] = useState('');
  const [specialRequests, setSpecialRequests] = useState('');
  const [bookingStatus, setBookingStatus] = useState('');

  const handleReservation = (e: React.FormEvent) => {
    e.preventDefault();
    setBookingStatus(`Reservation confirmed for ${guests} guests on ${date} at ${time}`);
  };

  return (
    <div className="min-h-screen p-8 bg-orange-100">
      <div className="max-w-4xl mx-auto bg-white p-6 rounded-lg shadow-md">
        <h1 className="text-4xl font-bold mb-6 text-orange-600">{{reservation.title}}</h1>
        <form onSubmit={handleReservation} className="space-y-4">
          <input type="date" value={date} onChange={(e) => setDate(e.target.value)} className="w-full p-2 border border-gray-300 rounded" required />
          <select value={time} onChange={(e) => setTime(e.target.value)} className="w-full p-2 border border-gray-300 rounded" required>
            <option value="" disabled>Select Time</option>
            {availableSlots.map((slot, index) => (<option key={index} value={slot}>{slot}</option>))}
          </select>
          <input type="number" value={guests} onChange={(e) => setGuests(Number(e.target.value))} min={1} className="w-full p-2 border border-gray-300 rounded" required />
          <input type="text" value={table} onChange={(e) => setTable(e.target.value)} placeholder="Preferred Table" className="w-full p-2 border border-gray-300 rounded" />
          <textarea value={specialRequests} onChange={(e) => setSpecialRequests(e.target.value)} placeholder="Special Requests" className="w-full p-2 border border-gray-300 rounded" />
          <button type="submit" className="w-full p-2 bg-orange-600 text-white rounded hover:bg-orange-500">{{reservation.cta}}</button>
        </form>
        {bookingStatus && <p className="mt-4 text-green-600">{bookingStatus}</p>}
        <Link to="/" className="mt-4 inline-block text-blue-500 hover:underline">Back to Home</Link>
      </div>
    </div>
  );
};

export default Reservation;

import React from 'react';
import { Link } from 'react-router-dom';

interface ChefProfileProps {
  name: string; photo: string; biography: string; specialties: string[];
  philosophy: string; awards: string[]; signatureDishes: string[];
}

const ChefProfile: React.FC<ChefProfileProps> = ({ name, photo, biography, specialties, philosophy, awards, signatureDishes }) => {
  return (
    <div className="min-h-screen p-8 bg-orange-100">
      <div className="max-w-4xl mx-auto bg-white rounded-lg shadow-md p-6">
        <h1 className="text-4xl font-bold text-orange-600 mb-4">{name}</h1>
        <img src={photo} alt={`${name}'s profile`} className="w-full h-64 object-cover rounded-lg mb-4" />
        <p className="text-gray-700 mb-4">{biography}</p>
        <h2 className="text-2xl font-semibold text-orange-500 mb-2">Specialties</h2>
        <ul className="list-disc list-inside mb-4">{specialties.map((s, i) => (<li key={i} className="text-gray-600">{s}</li>))}</ul>
        <h2 className="text-2xl font-semibold text-orange-500 mb-2">Cooking Philosophy</h2>
        <p className="text-gray-700 mb-4">{philosophy}</p>
        <h2 className="text-2xl font-semibold text-orange-500 mb-2">Awards & Recognition</h2>
        <ul className="list-disc list-inside mb-4">{awards.map((a, i) => (<li key={i} className="text-gray-600">{a}</li>))}</ul>
        <h2 className="text-2xl font-semibold text-orange-500 mb-2">Signature Dishes</h2>
        <ul className="list-disc list-inside mb-4">{signatureDishes.map((d, i) => (<li key={i} className="text-gray-600">{d}</li>))}</ul>
        <Link to="/" className="text-blue-500 hover:underline">Back to Home</Link>
      </div>
    </div>
  );
};

export default ChefProfile;

import React from 'react';
import { Link } from 'react-router-dom';

interface Dish { id: number; name: string; description: string; ingredients: string[]; category: string; imageUrl: string; }

const dishes: Dish[] = {{gallery.dishes}};

const DishGallery: React.FC = () => {
  return (
    <div className="min-h-screen p-8 bg-white">
      <div className="max-w-6xl mx-auto">
        <h1 className="text-4xl font-bold mb-6 text-center text-blue-600">{{gallery.title}}</h1>
        <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-3 gap-6">
          {dishes.map((dish) => (
            <div key={dish.id} className="border rounded-lg shadow-lg overflow-hidden">
              <img src={dish.imageUrl} alt={dish.name} className="w-full h-48 object-cover" />
              <div className="p-4">
                <h2 className="text-2xl font-semibold mb-2">{dish.name}</h2>
                <p className="text-gray-700 mb-2">{dish.description}</p>
                <p className="text-gray-500">Ingredients: {dish.ingredients.join(', ')}</p>
                <Link to={`/dishes/${dish.id}`} className="text-blue-500 hover:underline mt-4 inline-block">View Details</Link>
              </div>
            </div>
          ))}
        </div>
      </div>
    </div>
  );
};

export default DishGallery;

import React from 'react';
import { Link } from 'react-router-dom';

interface HeaderProps { restaurantName: string; tagline?: string; }

const Header: React.FC<HeaderProps> = ({ restaurantName, tagline }) => {
  return (
    <header className="bg-orange-600 p-4 shadow-md">
      <div className="container mx-auto text-center">
        <Link to="/" className="text-decoration-none">
          <h1 className="text-3xl font-bold text-white">{restaurantName}</h1>
          {tagline && <p className="text-lg text-white mt-2">{tagline}</p>}
        </Link>
      </div>
    </header>
  );
};

export default Header;

import React from 'react';

interface FooterProps { restaurantName: string; address: string; phone: string; socialLinks: { platform: string; url: string }[]; }

const Footer: React.FC<FooterProps> = ({ restaurantName, address, phone, socialLinks }) => {
  return (
    <footer className="bg-orange-600 text-white p-6">
      <div className="container mx-auto text-center">
        <h2 className="text-2xl font-bold mb-2">{restaurantName}</h2>
        <p className="mb-2">{address}</p>
        <p className="mb-4">{phone}</p>
        <div className="flex justify-center space-x-4">
          {socialLinks.map((link) => (
            <a key={link.platform} href={link.url} className="text-white hover:text-orange-300" target="_blank" rel="noopener noreferrer">{link.platform}</a>
          ))}
        </div>
        <p className="mt-4 text-sm">© {new Date().getFullYear()} {restaurantName}. All rights reserved.</p>
      </div>
    </footer>
  );
};

export default Footer;

body { margin: 0; padding: 0; font-family: 'Inter', -apple-system, BlinkMacSystemFont, 'Segoe UI', 'Roboto', sans-serif; }
.text-decoration-none { text-decoration: none; }
.text-decoration-none:hover { text-decoration: none; }
