import React from 'react';
import ReactDOM from 'react-dom/client';
import { BrowserRouter } from 'react-router-dom';
import App from './App';
import './index.css';

const rootElement = document.getElementById('root');
if (!rootElement) throw new Error('Failed to find the root element');

const root = ReactDOM.createRoot(rootElement);
root.render(
  <React.StrictMode>
    <BrowserRouter>
      <App />
    </BrowserRouter>
  </React.StrictMode>
);

import React from 'react';
import { Routes, Route } from 'react-router-dom';
import Header from './components/Header';
import Footer from './components/Footer';
import Home from './pages/Home';
import About from './pages/About';
import Contact from './pages/Contact';
import Products from './pages/Products';
import ProductDetail from './pages/ProductDetail';
import Cart from './pages/Cart';
import Checkout from './pages/Checkout';
import NotFound from './pages/NotFound';

const App: React.FC = () => {
  return (
    <div className="<tw/>">
      <Header 
        brandName="{{header.brandName}}" 
        tagline="{{header.tagline}}" 
        logoUrl="{{header.logoUrl}}"
      />
      <main className="<tw/>">
        <Routes>
          <Route path="/" element={<Home />} />
          <Route path="/about" element={<About />} />
          <Route path="/contact" element={<Contact />} />
          <Route path="/products" element={<Products />} />
          <Route path="/products/:slug" element={<ProductDetail />} />
          <Route path="/cart" element={<Cart />} />
          <Route path="/checkout" element={<Checkout />} />
          <Route path="*" element={<NotFound />} />
        </Routes>
      </main>
      <Footer 
        columns={{footer.columns}} 
        newsletter={{footer.newsletter}} 
        brandName="{{header.brandName}}"
        socialLinks={{footer.socialLinks}}
      />
    </div>
  );
};

export default App;

@tailwind base;
@tailwind components;
@tailwind utilities;

@layer base {
  html { 
    font-size: <data key="typography.baseFontSize"/>px; 
    line-height: 1.5; 
    color: #111827; 
    background-color: <data key="theme.backgroundColor"/>; 
  }
  body { 
    font-family: <data key="typography.fontFamily"/>, system-ui, -apple-system, 'Segoe UI', Roboto, 'Helvetica Neue', Arial, 'Noto Sans', 'Apple Color Emoji', 'Segoe UI Emoji'; 
    margin: 0; 
    padding: 0; 
  }
  h1, h2, h3, h4, h5, h6 { margin: 0; font-weight: 700; }
  p { margin: 0; }
}

@layer components {
  .btn { @apply px-4 py-2 rounded-lg text-white <tw/> transition duration-300 ease-in-out; }
  .btn-secondary { @apply px-4 py-2 rounded-lg border-2 <tw/> transition duration-300 ease-in-out; }
  .card { @apply bg-white shadow-md rounded-lg p-6 mb-4; }
  .header { @apply <tw/>; }
  .footer { @apply <tw/> text-gray-200 p-6 text-center; }
  .container { @apply max-w-7xl mx-auto px-4 sm:px-6 lg:px-8; }
  .section { @apply py-12 lg:py-16; }
  .heading { @apply text-3xl lg:text-4xl font-bold mb-6 text-center; }
  .subheading { @apply text-xl text-gray-600 mb-8 text-center max-w-3xl mx-auto; }
}

import React from 'react';
import { Link } from 'react-router-dom';

const NotFound: React.FC = () => {
  return (
    <div className="min-h-screen flex items-center justify-center bg-gray-50">
      <div className="text-center">
        <h1 className="text-6xl font-bold text-gray-900 mb-4">404</h1>
        <h2 className="text-2xl font-semibold text-gray-700 mb-4"><text/></h2>
        <p className="text-gray-600 mb-8 max-w-md mx-auto"><text/></p>
        <div className="space-x-4">
          <Link to="/" className="btn"><text/></Link>
          <Link to="/products" className="btn-secondary"><text/></Link>
        </div>
      </div>
    </div>
  );
};

export default NotFound;

import React, { useState } from 'react';
import { Link, useLocation } from 'react-router-dom';

interface HeaderProps { 
  brandName: string; 
  tagline?: string; 
  logoUrl?: string;
}

const Header: React.FC<HeaderProps> = ({ brandName, tagline, logoUrl }) => {
  const [isMenuOpen, setIsMenuOpen] = useState(false);
  const location = useLocation();

  const navigation = [
    { name: 'หน้าแรก', href: '/' },
    { name: 'เกี่ยวกับเรา', href: '/about' },
    { name: 'สินค้า', href: '/products' },
    { name: 'ติดต่อ', href: '/contact' }
  ];

  return (
    <header className="header sticky top-0 z-50">
      <div className="container">
        <div className="flex items-center justify-between py-4">
          {/* Logo */}
          <Link to="/" className="flex items-center space-x-3">
            {logoUrl && (
              <img src={logoUrl} alt={brandName} className="h-10 w-auto" />
            )}
            <div>
              <h1 className="brand">{brandName}</h1>
              {tagline && <p className="text-sm text-gray-500">{tagline}</p>}
            </div>
          </Link>

          {/* Desktop Navigation */}
          <nav className="hidden md:flex items-center space-x-8">
            {navigation.map((item) => (
              <Link
                key={item.name}
                to={item.href}
                className={`nav ${location.pathname === item.href 
                  ? 'text-blue-600 font-medium' 
                  : 'text-gray-700 hover:text-gray-900'}`}
              >
                {item.name}
              </Link>
            ))}
            <Link to="/cart" className="nav flex items-center space-x-2">
              <span><text/></span>
              <span className="<tw/>"><text/></span>
            </Link>
          </nav>

          {/* Mobile menu button */}
          <button
            onClick={() => setIsMenuOpen(!isMenuOpen)}
            className="md:hidden p-2 rounded-md text-gray-700 hover:text-gray-900 focus:outline-none focus:ring-2 focus:ring-blue-500"
          >
            <span className="sr-only">เปิดเมนู</span>
            <svg className="h-6 w-6" fill="none" viewBox="0 0 24 24" stroke="currentColor">
              <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M4 6h16M4 12h16M4 18h16" />
            </svg>
          </button>
        </div>

        {/* Mobile Navigation */}
        {isMenuOpen && (
          <div className="md:hidden border-t border-gray-200 py-4">
            <nav className="flex flex-col space-y-2">
              {navigation.map((item) => (
                <Link
                  key={item.name}
                  to={item.href}
                  onClick={() => setIsMenuOpen(false)}
                  className={`px-3 py-2 rounded-md text-base font-medium ${
                    location.pathname === item.href
                      ? 'text-blue-600 bg-blue-50'
                      : 'text-gray-700 hover:text-gray-900 hover:bg-gray-50'
                  }`}
                >
                  {item.name}
                </Link>
              ))}
              <Link
                to="/cart"
                onClick={() => setIsMenuOpen(false)}
                className="px-3 py-2 rounded-md text-base font-medium text-gray-700 hover:text-gray-900 hover:bg-gray-50"
              >
                <text/>
              </Link>
            </nav>
          </div>
        )}
      </div>
    </header>
  );
};

export default Header;

import React from 'react';
import { Link } from 'react-router-dom';

interface FooterProps { 
  brandName: string; 
  columns: { title: string; links: { label: string; href: string }[] }[]; 
  newsletter?: { enabled: boolean }; 
  socialLinks?: { platform: string; url: string; icon: string }[];
}

const Footer: React.FC<FooterProps> = ({ brandName, columns, newsletter, socialLinks }) => {
  return (
    <footer className="footer">
      <div className="container">
        <div className="grid grid-cols-1 md:grid-cols-4 gap-8 mb-8">
          {/* Brand */}
          <div className="md:col-span-1">
            <h3 className="text-lg font-semibold mb-4">{brandName}</h3>
            <p className="text-gray-400 text-sm mb-4"><text/></p>
            {socialLinks && (
              <div className="flex space-x-4">
                {socialLinks.map((social, index) => (
                  <a
                    key={index}
                    href={social.url}
                    target="_blank"
                    rel="noopener noreferrer"
                    className="text-gray-400 hover:text-white transition-colors"
                  >
                    <span className="sr-only">{social.platform}</span>
                    <span className="text-xl">{social.icon}</span>
                  </a>
                ))}
              </div>
            )}
          </div>

          {/* Links */}
          {columns.map((col, idx) => (
            <div key={idx} className="md:col-span-1">
              <h4 className="text-lg font-semibold mb-4">{col.title}</h4>
              <ul className="space-y-2">
                {col.links.map((link, i) => (
                  <li key={i}>
                    <Link to={link.href} className="link">
                      {link.label}
                    </Link>
                  </li>
                ))}
              </ul>
            </div>
          ))}
        </div>

        {/* Newsletter */}
        {newsletter?.enabled && (
          <div className="border-t border-gray-700 pt-8 mb-8">
            <div className="max-w-md mx-auto text-center">
              <h4 className="text-lg font-semibold mb-4"><text/></h4>
              <p className="text-gray-400 text-sm mb-4"><text/></p>
              <div className="flex">
                <input
                  type="email"
                  placeholder="<text/>"
                  className="flex-1 px-4 py-2 rounded-l-lg text-gray-900 focus:outline-none focus:ring-2 focus:ring-blue-500"
                />
                <button className="btn rounded-l-none"><text/></button>
              </div>
            </div>
          </div>
        )}

        {/* Copyright */}
        <div className="border-t border-gray-700 pt-8 text-center">
          <p className="text-sm text-gray-400">
            © {new Date().getFullYear()} {brandName}. <text/>
          </p>
        </div>
      </div>
    </footer>
  );
};

export default Footer;
