export const MediaType = {
  Image: 'image',
  Video: 'video',
  GIF: 'gif',
};

export const formatDate = (date) => {
  return date.toLocaleDateString('en-US', {
    year: 'numeric',
    month: 'long',
    day: 'numeric',
  });
};

export const filterImagesByTag = (images, tag) => {
  return images.filter(image => image.tags?.includes(tag));
};

export const paginateImages = (images, currentPage, itemsPerPage) => {
  const startIndex = (currentPage - 1) * itemsPerPage;
  return images.slice(startIndex, startIndex + itemsPerPage);
};

export const generateUniqueId = () => {
  return 'img-' + Math.random().toString(36).substr(2, 9);
};
