{
  "name": "simple-blog-platform",
  "version": "1.0.0",
  "private": true,
  "scripts": {
    "dev": "vite",
    "build": "vite build",
    "serve": "vite preview"
  },
  "dependencies": {
    "react": "^18.0.0",
    "react-dom": "^18.0.0",
    "react-router-dom": "^6.0.0"
  },
  "devDependencies": {
    "vite": "^4.0.0",
    "typescript": "^4.0.0",
    "eslint": "^8.0.0",
    "eslint-plugin-react": "^7.0.0",
    "eslint-plugin-react-hooks": "^4.0.0",
    "tailwindcss": "^3.0.0",
    "postcss": "^8.0.0",
    "autoprefixer": "^10.0.0"
  },
  "eslintConfig": {
    "extends": [
      "plugin:react/recommended",
      "plugin:react-hooks/recommended"
    ],
    "parser": "@typescript-eslint/parser",
    "parserOptions": {
      "ecmaVersion": 2020,
      "sourceType": "module"
    },
    "rules": {
      "react/react-in-jsx-scope": "off"
    }
  },
  "browserslist": [
    ">0.2%",
    "not dead",
    "not op_mini all"
  ]
}

{
  " doctype" : " html" ,
  " html" : {
    " head" : {
      " title" : " Simple Blog Platform" ,
      " meta" : [
      {
        " charset" : " UTF-8" },
      {
        " name" : " viewport" ,
        " content" : " width=" device-width, initial-scale=" 1.0" },
      {
        " name" : " description" ,
        " content" : " A simple blog platform built with Vite and React." }
      ],
      " link" : [
      {
        " rel" : " icon" ,
        " href" : " /favicon.ico" },
      {
        " rel" : " stylesheet" ,
        " href" : " https://cdnjs.cloudflare.com/ajax/libs/tailwindcss/2.2.19/tailwind.min.css" }
      ]
    },
    " body" : {
      " div" : {
        " id" : " app" },
      " script" : {
        " type" : " module" ,
        " src" : " /src/main.tsx" }
    }
  }
}

@tailwind base;
  @tailwind components;
  @tailwind utilities;
  body {
  @apply bg-gray-50 text-gray-800 font-sans leading-normal tracking-normal;
  }
h1, h2, h3, h4, h5, h6 {
  @apply font-bold text-gray-900;
  }
a {
  @apply text-blue-600 hover: text-blue-800 transition-colors duration-200;
  }
button {
  @apply bg-blue-500 text-white font-semibold py-2 px-4 rounded shadow hover: bg-blue-600 transition-colors duration-200;
  }
.container {
  @apply mx-auto px-4 sm: px-6 lg: px-8;
  }
.card {
  @apply bg-white shadow-md rounded-lg p-6 mb-4;
  }
.card-header {
  @apply border-b border-gray-200 pb-2 mb-4;
  }
.card-body {
  @apply text-gray-700;
  }
.footer {
  @apply bg-gray-100 text-center py-4 mt-8;
  }
@media (min-width: 640px) {
  .card {
  @apply flex flex-col md: flex-row;
  }

}
@media (max-width: 768px) {
  .header {
  @apply text-center;
  }

}
