export type FetchOptions = {
  method?: 'GET' | 'POST' | 'PUT' | 'DELETE';
  headers?: Record<string, string>;
  body?: string;
};

export const fetchData = async <T>(url: string, options?: FetchOptions): Promise<ApiResponse<T>> => {
  try {
    const response = await fetch(url, {
      method: options?.method || 'GET',
      headers: {
        'Content-Type': 'application/json',
        ...options?.headers,
      },
      body: options?.body,
    });

    if (!response.ok) {
      throw new Error('Network response was not ok');
    }

    const data: T = await response.json();
    return { data };
  } catch (error) {
    return { data: null as unknown as T, error: error instanceof Error ? error.message : 'Unknown error' };
  }
};

export const formatDate = (date: Date, format: string): string => {
  const options: Intl.DateTimeFormatOptions = {};

  if (format.includes('year')) options.year = 'numeric';
  if (format.includes('month')) options.month = 'long';
  if (format.includes('day')) options.day = 'numeric';

  return new Intl.DateTimeFormat('en-US', options).format(date);
};
