@tailwind base;
@tailwind components;
@tailwind utilities;

@layer base {
  body {
    @apply bg-white text-gray-800 font-sans leading-normal tracking-normal;
  }

  h1, h2, h3, h4, h5, h6 {
    @apply font-bold text-gray-900;
  }

  p {
    @apply mb-4;
  }

  a {
    @apply text-blue-600 hover:text-blue-800;
  }

  button {
    @apply bg-blue-600 text-white font-semibold py-2 px-4 rounded transition duration-200 ease-in-out;
  }

  button:hover {
    @apply bg-blue-700;
  }
}

@layer components {
  .card {
    @apply bg-white shadow-lg rounded-lg p-6 mb-6 transition-transform transform hover:scale-105;
  }

  .header {
    @apply bg-blue-600 text-white p-4 rounded-t-lg;
  }

  .footer {
    @apply bg-gray-100 text-gray-600 p-4 rounded-b-lg;
  }

  .input {
    @apply border border-gray-300 rounded-lg p-2 focus:outline-none focus:ring-2 focus:ring-blue-500;
  }

  .input-error {
    @apply border-red-500;
  }
}

@layer utilities {
  .text-vibrant {
    @apply text-red-600;
  }

  .bg-vibrant {
    @apply bg-yellow-400;
  }

  @media (max-width: 640px) {
    .responsive-text {
      @apply text-sm;
    }
  }

  @media (min-width: 640px) {
    .responsive-text {
      @apply text-base;
    }
  }

  @media (min-width: 768px) {
    .responsive-text {
      @apply text-lg;
    }
  }

  @media (min-width: 1024px) {
    .responsive-text {
      @apply text-xl;
    }
  }
}

