  onQuickView: () => void;
}

const ProductCard: React.FC<ProductCardProps> = ({
  name,
  price,
  images,
  sizes,
  colors,
  ratings,
  reviewsCount,
  onAddToWishlist,
  onQuickView,
}) => {
  return (
    <div className="p-6 bg-white rounded-lg shadow-md">
      <div className="flex flex-col">
        <img src={images[0]} alt={name} className="w-full h-48 object-cover rounded" />
        <h2 className="text-xl font-semibold mt-4">{name}</h2>
        <p className="text-lg font-bold text-red-600">${price.toFixed(2)}</p>
        <div className="mt-2">
          <span className="text-gray-600">Sizes:</span>
          <ul className="list-disc pl-5">
            {sizes.map((size) => (
              <li key={size} className="text-gray-800">{size}</li>
            ))}
          </ul>
        </div>
        <div className="mt-2">
          <span className="text-gray-600">Colors:</span>
          <ul className="flex space-x-2">
            {colors.map((color) => (
              <li key={color} className={`w-6 h-6 rounded-full`} style={{ backgroundColor: color }}></li>
            ))}
          </ul>
        </div>
        <div className="mt-2">
          <span className="text-gray-600">Rating:</span>
          <span className="text-yellow-500">{'★'.repeat(Math.round(ratings))}</span>
          <span className="text-gray-600"> ({reviewsCount} reviews)</span>
        </div>
        <div className="mt-4 flex space-x-2">
          <button 
            onClick={onAddToWishlist} 
            className="bg-blue-500 text-white px-4 py-2 rounded"
          >
            Add to Wishlist
          </button>
          <button 
            onClick={onQuickView} 
            className="bg-green-500 text-white px-4 py-2 rounded"
          >
            Quick View
          </button>
        </div>
      </div>
    </div>
  );
};

export default ProductCard;

