export type ApiResponse<T> = {
  data: T;
  message: string;
  status: number;
};

export type FetchOptions = {
  method?: 'GET' | 'POST' | 'PUT' | 'DELETE';
  headers?: Record<string, string>;
  body?: string;
};

export const fetchJson = async <T>(url: string, options?: FetchOptions): Promise<ApiResponse<T>> => {
  const response = await fetch(url, {
    ...options,
    headers: {
      'Content-Type': 'application/json',
      ...options?.headers,
    },
  });
  const data = await response.json();
  return {
    data,
    message: response.statusText,
    status: response.status,
  };
};

export const isUser = (user: any): user is User => {
  return (
    typeof user.id === 'number' &&
    typeof user.name === 'string' &&
    typeof user.email === 'string' &&
    user.createdAt instanceof Date
  );
};

export interface User {
  id: number;
  name: string;
  email: string;
}

export interface ApiResponse<T> {
  data: T;
  error?: string;
}

export type FetchOptions = {
  method?: string;
  headers?: Record<string, string>;
  body?: string;
};

export const fetchData = async <T>(url: string, options?: FetchOptions): Promise<ApiResponse<T>> => {
  try {
    const response = await fetch(url, {
      method: options?.method || 'GET',
      headers: {
        'Content-Type': 'application/json',
        ...options?.headers,
      },
      body: options?.body,
    });

    if (!response.ok) {
      throw new Error('Network response was not ok');
    }

    const data: T = await response.json();
    return { data };
  } catch (error) {
    return { data: null as unknown as T, error: error instanceof Error ? error.message : 'Unknown error' };
  }
};

export const debounce = (func: (...args: any[]) => void, delay: number) => {
  let timeoutId: NodeJS.Timeout;
  return (...args: any[]) => {
    if (timeoutId) {
      clearTimeout(timeoutId);
    }
    timeoutId = setTimeout(() => {
      func(...args);
    }, delay);
  };
};
