import React, { useState } from 'react';

interface ContactFormProps {
  onSubmit: (formData: { name: string; email: string; message: string }) => void;
}

const ContactForm: React.FC<ContactFormProps> = ({ onSubmit }) => {
  const [name, setName] = useState<string>('');
  const [email, setEmail] = useState<string>('');
  const [message, setMessage] = useState<string>('');

  const handleSubmit = (event: React.FormEvent) => {
    event.preventDefault();
    onSubmit({ name, email, message });
    setName('');
    setEmail('');
    setMessage('');
  };

  return (
    <div className="p-6 bg-neutral-100 rounded-lg shadow-md">
      <h2 className="text-2xl font-semibold mb-4">Contact Us</h2>
      <form onSubmit={handleSubmit} className="flex flex-col">
        <label className="mb-2">
          <span className="block text-gray-700">Name</span>
          <input
            type="text"
            value={name}
            onChange={(e) => setName(e.target.value)}
            required
            className="mt-1 p-2 border border-gray-300 rounded"
          />
        </label>
        <label className="mb-2">
          <span className="block text-gray-700">Email</span>
          <input
            type="email"
            value={email}
            onChange={(e) => setEmail(e.target.value)}
            required
            className="mt-1 p-2 border border-gray-300 rounded"
          />
        </label>
        <label className="mb-2">
          <span className="block text-gray-700">Message</span>
          <textarea
            value={message}
            onChange={(e) => setMessage(e.target.value)}
            required
            className="mt-1 p-2 border border-gray-300 rounded"
          />
        </label>
        <button
          type="submit"
          className="mt-4 bg-brown-500 text-white px-4 py-2 rounded hover:bg-brown-600"
        >
          Send Message
        </button>
      </form>
    </div>
  );
};

export default ContactForm;

