import { defineConfig } from 'vite'
import react from '@vitejs/plugin-react'

export default defineConfig({
  plugins: [react()],
})

import React from 'react';
import ReactDOM from 'react-dom/client';
import { BrowserRouter } from 'react-router-dom';
import App from './App';
import './index.css';

const rootElement = document.getElementById('root');
if (!rootElement) throw new Error('Failed to find the root element');

const root = ReactDOM.createRoot(rootElement);
root.render(
  <React.StrictMode>
    <BrowserRouter>
      <App />
    </BrowserRouter>
  </React.StrictMode>
);

import React from 'react';
import { Routes, Route } from 'react-router-dom';
import Header from './components/Header';
import Footer from './components/Footer';
import Home from './pages/Home';
import Products from './pages/Products';
import ProductDetail from './pages/ProductDetail';
import Cart from './pages/Cart';
import Checkout from './pages/Checkout';

const App: React.FC = () => {
  return (
    <div className="min-h-screen flex flex-col bg-gray-50">
      <Header brandName="Café Delight" tagline="กาแฟสดใหม่ทุกวัน" />
      <main className="flex-1">
        <Routes>
          <Route path="/" element={<Home />} />
          <Route path="/products" element={<Products />} />
          <Route path="/products/:slug" element={<ProductDetail />} />
          <Route path="/cart" element={<Cart />} />
          <Route path="/checkout" element={<Checkout />} />
        </Routes>
      </main>
      <Footer columns={[{"title":"เกี่ยวกับเรา","links":[{"label":"ประวัติ","href":"/about"},{"label":"ทีมงาน","href":"/team"}]},{"title":"บริการ","links":[{"label":"จัดส่ง","href":"/delivery"},{"label":"ติดต่อ","href":"/contact"}]},{"title":"ข้อมูล","links":[{"label":"เงื่อนไข","href":"/terms"},{"label":"นโยบาย","href":"/privacy"}]}]} newsletter={{"enabled":true}} brandName="Café Delight" />
    </div>
  );
};

export default App;

@tailwind base;
@tailwind components;
@tailwind utilities;

@layer base {
  html { font-size: 16px; line-height: 1.5; color: #111827; background-color: #f9fafb; }
  body { font-family: 'Inter', system-ui, -apple-system, 'Segoe UI', Roboto, 'Helvetica Neue', Arial, 'Noto Sans', 'Apple Color Emoji', 'Segoe UI Emoji'; margin: 0; padding: 0; }
  h1, h2, h3, h4, h5, h6 { margin: 0; font-weight: 700; }
  p { margin: 0; }
}

@layer components {
  .btn { @apply px-4 py-2 rounded-lg text-white bg-green-600 hover:bg-green-500 transition duration-300 ease-in-out; }
  .card { @apply bg-white shadow-md rounded-lg p-6 mb-4; }
  .header { @apply bg-white border-b; }
  .footer { @apply bg-gray-900 text-gray-200 p-6 text-center; }
}

import React from 'react';
import { Link } from 'react-router-dom';

const Home: React.FC = () => {
  return (
    <div className="min-h-screen p-8">
      <div className="max-w-6xl mx-auto text-center">
        <h1 className="text-5xl font-extrabold mb-4 text-green-700">กาแฟสดใหม่ทุกวัน</h1>
        <p className="text-xl text-gray-700 mb-8">สัมผัสรสชาติกาแฟคุณภาพสูงจากเมล็ดคั่วสดใหม่ พร้อมขนมหวานแสนอร่อย</p>
        <Link to="/products" className="btn text-lg px-8 py-3">เริ่มช้อปเลย</Link>
        <div className="grid grid-cols-1 md:grid-cols-3 gap-6 mt-12">
          <div className="card">
            <h3 className="text-xl font-semibold mb-2">กาแฟสดใหม่</h3>
            <p className="text-gray-600">คั่วเมล็ดกาแฟสดใหม่ทุกวัน เพื่อรสชาติที่สมบูรณ์แบบ</p>
          </div>
          <div className="card">
            <h3 className="text-xl font-semibold mb-2">ขนมหวานอร่อย</h3>
            <p className="text-gray-600">ขนมหวานทำสดใหม่ทุกเช้า คู่กับกาแฟได้อย่างลงตัว</p>
          </div>
          <div className="card">
            <h3 className="text-xl font-semibold mb-2">บริการจัดส่ง</h3>
            <p className="text-gray-600">จัดส่งถึงบ้านฟรี เมื่อสั่งซื้อครบ 500 บาท</p>
          </div>
        </div>
      </div>
    </div>
  );
};

export default Home;

import React, { useMemo, useState } from 'react';
import ProductCard from '../components/ProductCard';

export interface Product { id: string; name: string; price: number; imageUrl: string; rating?: number; tags?: string[]; }

const Products: React.FC = () => {
  const [query, setQuery] = useState('');
  const [category, setCategory] = useState('all');
  const [sort, setSort] = useState('new');

  const products: Product[] = [{"id":"coffee-001","name":"กาแฟอเมริกาโน่","price":85,"imageUrl":"https://images.unsplash.com/photo-1495474472287-4d71bcdd2085?w=600&h=450&fit=crop","rating":4.5,"tags":["coffee","hot"]},{"id":"coffee-002","name":"ลาเต้","price":95,"imageUrl":"https://images.unsplash.com/photo-1561047029-3000c68339ca?w=600&h=450&fit=crop","rating":4.7,"tags":["coffee","milk"]},{"id":"coffee-003","name":"คาปูชิโน่","price":90,"imageUrl":"https://images.unsplash.com/photo-1572442388796-11668a67e53d?w=600&h=450&fit=crop","rating":4.6,"tags":["coffee","foam"]},{"id":"coffee-004","name":"มอคค่า","price":105,"imageUrl":"https://images.unsplash.com/photo-1559056199-641a0ac8b55e?w=600&h=450&fit=crop","rating":4.8,"tags":["coffee","chocolate"]},{"id":"coffee-005","name":"ชาเขียว","price":75,"imageUrl":"https://images.unsplash.com/photo-1556679343-c7306c1976bc?w=600&h=450&fit=crop","rating":4.3,"tags":["tea","green"]},{"id":"coffee-006","name":"ชานมไข่มุก","price":120,"imageUrl":"https://images.unsplash.com/photo-1525385133512-2f3bdd039054?w=600&h=450&fit=crop","rating":4.9,"tags":["tea","bubble"]}];
  const categories: { id: string; name: string }[] = [{"id":"all","name":"ทั้งหมด"},{"id":"coffee","name":"กาแฟ"},{"id":"tea","name":"ชา"},{"id":"hot","name":"เครื่องดื่มร้อน"},{"id":"cold","name":"เครื่องดื่มเย็น"}];

  const filtered = useMemo(() => {
    let list = products.filter(p => p.name.toLowerCase().includes(query.toLowerCase()));
    if (category !== 'all') list = list.filter(p => p.tags?.includes(category));
    if (sort === 'price-asc') list = list.slice().sort((a,b) => a.price - b.price);
    if (sort === 'price-desc') list = list.slice().sort((a,b) => b.price - a.price);
    return list;
  }, [products, query, category, sort]);

  return (
    <div className="min-h-screen p-8 bg-gray-100">
      <div className="max-w-6xl mx-auto">
        <div className="flex flex-col md:flex-row md:items-center md:justify-between gap-4 mb-6">
          <h1 className="text-3xl font-bold">เมนูเครื่องดื่ม</h1>
          <div className="flex gap-3">
            <input value={query} onChange={(e)=>setQuery(e.target.value)} placeholder="ค้นหาสินค้า..." className="border rounded px-3 py-2 w-60" />
            <select value={category} onChange={(e)=>setCategory(e.target.value)} className="border rounded px-3 py-2">
              <option value="all">ทั้งหมด</option>
              {categories.map(c => (<option key={c.id} value={c.id}>{c.name}</option>))}
            </select>
            <select value={sort} onChange={(e)=>setSort(e.target.value)} className="border rounded px-3 py-2">
              <option value="new">ใหม่ล่าสุด</option>
              <option value="price-asc">ราคาต่ำ-สูง</option>
              <option value="price-desc">ราคาสูง-ต่ำ</option>
            </select>
          </div>
        </div>
        <div className="grid grid-cols-1 sm:grid-cols-2 md:grid-cols-3 lg:grid-cols-4 gap-6">
          {filtered.map(p => (<ProductCard key={p.id} product={p} currency="฿" />))}
        </div>
      </div>
    </div>
  );
};

export default Products;

import React from 'react';
import { useParams, Link } from 'react-router-dom';

const ProductDetail: React.FC = () => {
  const { slug } = useParams();
  const product = {"id":"coffee-001","name":"กาแฟอเมริกาโน่","price":85,"imageUrl":"https://images.unsplash.com/photo-1495474472287-4d71bcdd2085?w=1200&h=675&fit=crop","description":"กาแฟอเมริกาโน่เข้มข้น รสชาติกลมกล่อม เหมาะสำหรับผู้ที่ชอบกาแฟดำแท้"}; // would be fetched by slug in real app
  if (!product) return <div className="p-8">ไม่พบสินค้า</div>;
  return (
    <div className="min-h-screen p-8 bg-white">
      <div className="max-w-5xl mx-auto grid grid-cols-1 md:grid-cols-2 gap-8">
        <img src={product.imageUrl} alt={product.name} className="w-full h-96 object-cover rounded-lg" />
        <div>
          <h1 className="text-3xl font-bold mb-2">{product.name}</h1>
          <p className="text-2xl text-green-700 font-semibold mb-4">฿{product.price.toLocaleString()}</p>
          <p className="text-gray-700 mb-6">{product.description}</p>
          <div className="flex gap-3">
            <button className="btn">หยิบใส่ตะกร้า</button>
            <Link to="/checkout" className="btn bg-blue-600 hover:bg-blue-500">ซื้อเลย</Link>
          </div>
        </div>
      </div>
      <div className="max-w-5xl mx-auto mt-12">
        <h2 className="text-2xl font-semibold mb-4">รายละเอียด</h2>
        <div className="prose"><p>กาแฟอเมริกาโน่ของเราทำจากเมล็ดกาแฟคุณภาพสูง คั่วสดใหม่ทุกวัน รสชาติเข้มข้นแต่ไม่ขม เหมาะสำหรับผู้ที่ชื่นชอบกาแฟดำแท้</p><ul><li>ใช้เมล็ดกาแฟอาราบิก้า 100%</li><li>คั่วสดใหม่ทุกวัน</li><li>เสิร์ฟร้อนหรือเย็น</li><li>ไม่มีน้ำตาล</li></ul></div>
      </div>
    </div>
  );
};

export default ProductDetail;

import React from 'react';
import CartSummary from '../components/CartSummary';

const Cart: React.FC = () => {
  const items = [{"id":"coffee-001","name":"กาแฟอเมริกาโน่","price":85,"qty":2,"imageUrl":"https://images.unsplash.com/photo-1495474472287-4d71bcdd2085?w=300&h=300&fit=crop"},{"id":"coffee-002","name":"ลาเต้","price":95,"qty":1,"imageUrl":"https://images.unsplash.com/photo-1561047029-3000c68339ca?w=300&h=300&fit=crop"}];
  const currency = "฿";
  const total = items.reduce((s: number, it: any) => s + it.price * it.qty, 0);
  return (
    <div className="min-h-screen p-8 bg-gray-50">
      <div className="max-w-5xl mx-auto">
        <h1 className="text-3xl font-bold mb-6">ตะกร้าสินค้า</h1>
        <div className="grid grid-cols-1 md:grid-cols-3 gap-6">
          <div className="md:col-span-2 space-y-4">
            {items.map((it: any) => (
              <div key={it.id} className="card flex items-center gap-4">
                <img src={it.imageUrl} alt={it.name} className="w-24 h-24 object-cover rounded" />
                <div className="flex-1">
                  <p className="font-medium">{it.name}</p>
                  <p className="text-sm text-gray-500">x{it.qty}</p>
                </div>
                <div className="font-semibold">{currency}{(it.price * it.qty).toLocaleString()}</div>
              </div>
            ))}
          </div>
          <CartSummary total={total} currency={currency} checkoutHref="/checkout" />
        </div>
      </div>
    </div>
  );
};

export default Cart;

import React, { useState } from 'react';

const Checkout: React.FC = () => {
  const [name, setName] = useState('');
  const [address, setAddress] = useState('');
  const [payment, setPayment] = useState('cod');
  const total = 265;
  const currency = "฿";
  const submit = (e: React.FormEvent) => { e.preventDefault(); alert('ชำระเงินสำเร็จ'); };
  return (
    <div className="min-h-screen p-8 bg-white">
      <div className="max-w-3xl mx-auto">
        <h1 className="text-3xl font-bold mb-6">ชำระเงิน</h1>
        <form onSubmit={submit} className="space-y-4">
          <input value={name} onChange={(e)=>setName(e.target.value)} placeholder="ชื่อ-นามสกุล" className="w-full p-2 border rounded" required />
          <textarea value={address} onChange={(e)=>setAddress(e.target.value)} placeholder="ที่อยู่จัดส่ง" className="w-full p-2 border rounded" required />
          <select value={payment} onChange={(e)=>setPayment(e.target.value)} className="w-full p-2 border rounded">
            <option value="cod">เก็บเงินปลายทาง</option>
            <option value="card">บัตรเครดิต/เดบิต</option>
            <option value="transfer">โอนเงิน</option>
          </select>
          <div className="flex items-center justify-between">
            <p className="text-lg">ยอดชำระ</p>
            <p className="text-2xl font-semibold text-green-700">{currency}{total.toLocaleString()}</p>
          </div>
          <button type="submit" className="btn w-full">ยืนยันการชำระเงิน</button>
        </form>
      </div>
    </div>
  );
};

export default Checkout;

import React from 'react';
import { Link } from 'react-router-dom';

interface HeaderProps { brandName: string; tagline?: string; }

const Header: React.FC<HeaderProps> = ({ brandName, tagline }) => {
  return (
    <header className="header">
      <div className="max-w-6xl mx-auto flex items-center justify-between p-4">
        <Link to="/" className="text-decoration-none">
          <h1 className="text-2xl font-bold text-gray-900">{brandName}</h1>
          {tagline && <p className="text-sm text-gray-500">{tagline}</p>}
        </Link>
        <nav className="flex items-center gap-4">
          <Link to="/products" className="text-gray-700 hover:text-gray-900">สินค้า</Link>
          <Link to="/cart" className="text-gray-700 hover:text-gray-900">ตะกร้า</Link>
        </nav>
      </div>
    </header>
  );
};

export default Header;

import React from 'react';

interface FooterProps { brandName: string; columns: { title: string; links: { label: string; href: string }[] }[]; newsletter?: { enabled: boolean }; }

const Footer: React.FC<FooterProps> = ({ brandName, columns, newsletter }) => {
  return (
    <footer className="footer">
      <div className="max-w-6xl mx-auto">
        <div className="grid grid-cols-1 md:grid-cols-3 gap-6">
          {columns.map((col, idx) => (
            <div key={idx}>
              <h3 className="text-lg font-semibold mb-2">{col.title}</h3>
              <ul className="space-y-1">
                {col.links.map((l, i) => (
                  <li key={i}><a className="hover:underline" href={l.href}>{l.label}</a></li>
                ))}
              </ul>
            </div>
          ))}
        </div>
        {newsletter?.enabled && (
          <div className="mt-6">
            <input className="px-3 py-2 rounded text-gray-900" placeholder="อีเมลสำหรับรับข่าวสาร" />
          </div>
        )}
        <p className="mt-6 text-sm text-gray-400">© {new Date().getFullYear()} {brandName}. All rights reserved.</p>
      </div>
    </footer>
  );
};

export default Footer;

import React from 'react';
import type { Product } from '../pages/Products';

const ProductCard: React.FC<{ product: Product; currency: string }> = ({ product, currency }) => {
  return (
    <div className="bg-white rounded-lg shadow hover:shadow-lg transition p-4">
      <img src={product.imageUrl} alt={product.name} className="w-full h-48 object-cover rounded" />
      <h3 className="mt-3 font-semibold">{product.name}</h3>
      <p className="text-green-700 font-bold">{currency}{product.price.toLocaleString()}</p>
      <button className="btn w-full mt-3">หยิบใส่ตะกร้า</button>
    </div>
  );
};

export default ProductCard;

import React from 'react';

const CartSummary: React.FC<{ total: number; currency: string; checkoutHref: string }> = ({ total, currency, checkoutHref }) => {
  return (
    <aside className="card h-fit">
      <div className="flex items-center justify-between mb-4">
        <p className="text-lg">ยอดรวม</p>
        <p className="text-2xl font-semibold text-green-700">{currency}{total.toLocaleString()}</p>
      </div>
      <a href={checkoutHref} className="btn w-full">ไปชำระเงิน</a>
    </aside>
  );
};

export default CartSummary;
