export interface User {
  id: number;
  name: string;
  email: string;
}

export interface Post {
  id: number;
  title: string;
  content: string;
  authorId: number;
}

export type ApiResponse<T> = {
  data: T;
  error?: string;
};

export function isUser(obj: any): obj is User {
  return (
    typeof obj === 'object' &&
    obj !== null &&
    'id' in obj &&
    'name' in obj &&
    'email' in obj
  );
}

export function isPost(obj: any): obj is Post {
  return (
    typeof obj === 'object' &&
    obj !== null &&
    'id' in obj &&
    'title' in obj &&
    'content' in obj &&
    'authorId' in obj
  );
}

export type Nullable<T> = T | null;

export interface User {
  id: number;
  name: string;
  email: string;
}

export interface Product {
  id: number;
  name: string;
  price: number;
}

export type ApiResponse<T> = {
  data: T;
  error?: string;
};

export function fetchUser(userId: number): Promise<ApiResponse<User>> {
  return fetch(`/api/users/${userId}`)
    .then(response => response.json())
    .then(data => ({ data }))
    .catch(error => ({ data: null, error: error.message }));
}

export function fetchProducts(): Promise<ApiResponse<Product[]>> {
  return fetch('/api/products')
    .then(response => response.json())
    .then(data => ({ data }))
    .catch(error => ({ data: [], error: error.message }));
}

export function isValidEmail(email: string): boolean {
  const emailRegex = /^[^\s@]+@[^\s@]+\.[^\s@]+$/;
  return emailRegex.test(email);
}
