import React, { useState } from 'react';

const CommentSection: React.FC = () => {
    const [comments, setComments] = useState<any[]>([]);
    const [newComment, setNewComment] = useState<string>('');

    const handleCommentSubmit = (e: React.FormEvent) => {
        e.preventDefault();
        if (newComment.trim()) {
            const comment = {
                id: Date.now(),
                text: newComment,
                timestamp: new Date().toLocaleString(),
                replies: []
            };
            setComments([...comments, comment]);
            setNewComment('');
        }
    };

    const handleReplySubmit = (commentId: number, replyText: string) => {
        const updatedComments = comments.map(comment => {
            if (comment.id === commentId) {
                comment.replies.push({
                    id: Date.now(),
                    text: replyText,
                    timestamp: new Date().toLocaleString()
                });
            }
            return comment;
        });
        setComments(updatedComments);
    };

    return (
        <div className="bg-gray-100 p-6 rounded-lg shadow-md">
            <h2 className="text-2xl font-bold text-gray-900 mb-4">Comments</h2>
            <form onSubmit={handleCommentSubmit} className="mb-4">
                <textarea
                    className="w-full p-4 border border-gray-300 rounded-lg focus:ring-2 focus:ring-blue-500 focus:border-transparent"
                    rows={3}
                    value={newComment}
                    onChange={(e) => setNewComment(e.target.value)}
                    placeholder="Write a comment..."
                />
                <button
                    type="submit"
                    className="mt-2 bg-blue-600 text-white px-4 py-2 rounded-lg hover:bg-blue-700 transition-colors"
                >
                    Submit
                </button>
            </form>
            <div className="space-y-4">
                {comments.map(comment => (
                    <div key={comment.id} className="bg-white p-4 rounded-lg shadow-sm">
                        <div className="flex items-center justify-between">
                            <span className="font-semibold text-gray-800">User Avatar</span>
                            <span className="text-sm text-gray-500">{comment.timestamp}</span>
                        </div>
                        <p className="mt-2 text-gray-700">{comment.text}</p>
                        <button
                            onClick={() => handleReplySubmit(comment.id, 'Reply text')}
                            className="mt-2 text-blue-600 hover:underline"
                        >
                            Reply
                        </button>
                        {comment.replies.length > 0 && (
                            <div className="ml-4 mt-2 space-y-2">
                                {comment.replies.map(reply => (
                                    <div key={reply.id} className="bg-gray-50 p-2 rounded-lg shadow-sm">
                                        <span className="font-semibold text-gray-800">User Avatar</span>
                                        <p className="text-gray-700">{reply.text}</p>
                                        <span className="text-sm text-gray-500">{reply.timestamp}</span>
                                    </div>
                                ))}
                            </div>
                        )}
                    </div>
                ))}
            </div>
        </div>
    );
};

export default CommentSection;

