export default CategoryList;

export default {
  content: ['./index.html', './src/**/*.{tsx,ts,jsx,js}'],
  theme: {
    extend: {},
  },
  plugins: [],
}

export interface User {
  id: string;
  name: string;
  email: string;
  createdAt: Date;
}

export interface Post {
  id: string;
  title: string;
  content: string;
  authorId: string;
  createdAt: Date;
}

export type UserRole = 'admin' | 'editor' | 'viewer';

export interface ApiResponse<T> {
  data: T;
  message: string;
  success: boolean;
}

export const isUser = (user: any): user is User => {
  return (
    typeof user === 'object' &&
    user !== null &&
    typeof user.id === 'string' &&
    typeof user.name === 'string' &&
    typeof user.email === 'string' &&
    user.createdAt instanceof Date
  );
};

export const isPost = (post: any): post is Post => {
  return (
    typeof post === 'object' &&
    post !== null &&
    typeof post.id === 'string' &&
    typeof post.title === 'string' &&
    typeof post.content === 'string' &&
    typeof post.authorId === 'string' &&
    post.createdAt instanceof Date
  );
};

import { ReactNode } from 'react';

export interface User {
  id: string;
  name: string;
  email: string;
}

export interface ApiResponse<T> {
  data: T;
  error?: string;
}

export type Nullable<T> = T | null;

export function isNull<T>(value: T): value is Nullable<T> {
  return value === null;
}

export function fetchJson<T>(url: string): Promise<ApiResponse<T>> {
  return fetch(url)
    .then((response) => {
      if (!response.ok) {
        throw new Error('Network response was not ok');
      }
      return response.json();
    })
    .then((data: T) => ({ data }))
    .catch((error) => ({ data: null, error: error.message }));
}

export function renderWithFallback<T>(value: Nullable<T>, fallback: ReactNode): ReactNode {
  return isNull(value) ? fallback : value;
}
