import { Dispatch, SetStateAction } from 'react';

// Interface for a user
export interface User {
  id: number;
  name: string;
  email: string;
}

// Type for a function that sets a state of type T
export type SetState<T> = Dispatch<SetStateAction<T>>;

// Utility function to capitalize a string
export const capitalizeString = (str: string): string => {
  return str.charAt(0).toUpperCase() + str.slice(1);
};

// Utility function to check if a value is an object
export const isObject = (value: unknown): value is Record<string, unknown> => {
  return typeof value === 'object' && value !== null;
};

import { Dispatch, SetStateAction } from 'react';

// Interface for a user
export interface User {
  id: number;
  name: string;
  email: string;
}

// Type for a function that sets user state
export type SetUser = Dispatch<SetStateAction<User | null>>;

// Utility function to fetch user data
export const fetchUser = async (userId: number): Promise<User> => {
  // Fetch user data from API
  const response = await fetch(`https://api.example.com/users/${userId}`);
  const data = await response.json();

  return data;
};
