import ReactDOM from 'react-dom/client';
import { BrowserRouter } from 'react-router-dom';
import App from './App.tsx';
import './index.css';

const root = ReactDOM.createRoot(document.getElementById('root'));

root.render(
  <React.StrictMode>
    <BrowserRouter>
      <App />
    </BrowserRouter>
  </React.StrictMode>
);

import React from 'react';
import { Routes, Route } from 'react-router-dom';
import Header from './components/Header.tsx';
import Footer from './components/Footer.tsx';
import About from './pages/About.tsx';
import Contact from './pages/Contact.tsx';
import Home from './pages/Home.tsx';
import Articles from './pages/Articles.tsx';
import Article from './pages/Article.tsx';
import Categories from './pages/Categories.tsx';
import AdminDashboard from './pages/AdminDashboard.tsx';

const App: React.FC = () => {
  return (
    <div className="min-h-screen flex flex-col">
      <Header />
      <main className="flex-1">
        <Routes>
          <Route path="/" element={<About />} />
          <Route path="/about" element={<About />} />
          <Route path="/contact" element={<Contact />} />
          <Route path="/home" element={<Home />} />
          <Route path="/articles" element={<Articles />} />
          <Route path="/article" element={<Article />} />
          <Route path="/categories" element={<Categories />} />
          <Route path="/admindashboard" element={<AdminDashboard />} />
        </Routes>
      </main>
      <Footer />
    </div>
  );
};

export default App;

/* Apply a basic reset */
* {
  margin: 0;
  padding: 0;
  box-sizing: border-box;
}

/* Apply system font stack for better performance */
body {
  font-family: -apple-system, BlinkMacSystemFont, "Segoe UI", Roboto, "Helvetica Neue", Arial, "Noto Sans", sans-serif, "Apple Color Emoji", "Segoe UI Emoji", "Segoe UI Symbol", "Noto Color Emoji";
}

/* Set default text color and line height */
body {
  color: #333;
  line-height: 1.5;
}

/* Apply cool-blue-green color scheme */
body {
  --color-primary: #2b6cb0;
  --color-secondary: #2c7a7b;
  --color-accent: #319795;
}

/* Apply modern-minimal visual style */
body {
  background-color: #f7fafc;
}

/* Add more custom styles here */

import { defineConfig } from 'vite';

export default defineConfig({
  plugins: [
    // Add react-refresh plugin for React hot module replacement
    require('@vitejs/plugin-react-refresh')(),
  ],
});

import React from 'react';

const Footer: React.FC = () => {
  return (
    <footer className="bg-gray-800 text-white py-6">
      <div className="container mx-auto flex flex-col items-center">
        <p className="text-lg mb-4">Follow Us</p>
        <div className="flex space-x-4 mb-4">
          <a href="#" className="text-gray-400 hover:text-white transition-colors">
            <i className="fab fa-facebook"></i>
          </a>
          <a href="#" className="text-gray-400 hover:text-white transition-colors">
            <i className="fab fa-twitter"></i>
          </a>
          <a href="#" className="text-gray-400 hover:text-white transition-colors">
