export default HeroSection;

import React from 'react';

const ContactForm = () => {
  return (
    <div className="bg-white shadow-md rounded-lg p-6 md:p-8">
      <h2 className="text-2xl font-bold text-gray-900 mb-4">Contact Us</h2>
      <form className="grid grid-cols-1 gap-4 md:grid-cols-2">
        <input type="text" placeholder="Your Name" className="w-full px-4 py-2 border border-gray-300 rounded-lg focus:ring-2 focus:ring-blue-500 focus:border-transparent" />
        <input type="email" placeholder="Your Email" className="w-full px-4 py-2 border border-gray-300 rounded-lg focus:ring-2 focus:ring-blue-500 focus:border-transparent" />
        <textarea placeholder="Your Message" rows={4} className="w-full px-4 py-2 border border-gray-300 rounded-lg focus:ring-2 focus:ring-blue-500 focus:border-transparent"></textarea>
        <button type="submit" className="bg-blue-600 text-white px-6 py-3 rounded-lg hover:bg-blue-700 transition-colors font-medium">Submit</button>
      </form>
    </div>
  );
};

export default ContactForm;

