import React from 'react';

const CategoryList = () => {
  return (
    <div className="bg-white rounded-xl shadow-md p-6 hover:shadow-lg transition-shadow">
      <h2 className="text-2xl font-bold text-gray-900 mb-4">Categories</h2>
      <ul>
        <li className="flex items-center justify-between py-2 border-b border-gray-200">
          <div>
            <h3 className="text-lg font-semibold text-gray-800">Category 1</h3>
            <p className="text-sm text-gray-500">Category description goes here</p>
          </div>
          <span className="text-sm text-gray-600">10 articles</span>
        </li>
        <li className="flex items-center justify-between py-2 border-b border-gray-200">
          <div>
            <h3 className="text-lg font-semibold text-gray-800">Category 2</h3>
            <p className="text-sm text-gray-500">Another category description</p>
