import { Dispatch, SetStateAction } from 'react';

export interface User {
  id: number;
  name: string;
  email: string;
}

export type Theme = 'light' | 'dark';

export type Status = 'active' | 'inactive' | 'pending';

export type Nullable<T> = T | null;

export type Optional<T> = T | undefined;

export type UpdateState<T> = Dispatch<SetStateAction<T>>;

export function capitalizeString(str: string): string {
  return str.charAt(0).toUpperCase() + str.slice(1);
}

export function formatPhoneNumber(phoneNumber: number): string {
  return `(${phoneNumber.toString().slice(0, 3)}) ${phoneNumber.toString().slice(3, 6)}-${phoneNumber.toString().slice(6)}`;
}
