export interface User {
  id: string;
  name: string;
  email: string;
}

export interface Post {
  id: string;
  title: string;
  content: string;
  authorId: string;
}

export type Maybe<T> = T | null | undefined;

export type ApiResponse<T> = {
  data: T;
  error?: string;
};

export function isUser(obj: any): obj is User {
  return obj && typeof obj.id === 'string' && typeof obj.name === 'string' && typeof obj.email === 'string';
}

export function isPost(obj: any): obj is Post {
  return obj && typeof obj.id === 'string' && typeof obj.title === 'string' && typeof obj.content === 'string' && typeof obj.authorId === 'string';
}

export interface User {
  id: number;
  name: string;
  email: string;
}

export interface ApiResponse<T> {
  data: T;
  error?: string;
}

export type FetchOptions = {
  method?: 'GET' | 'POST' | 'PUT' | 'DELETE';
  headers?: Record<string, string>;
  body?: string;
};

export async function fetchData<T>(url: string, options?: FetchOptions): Promise<ApiResponse<T>> {
  try {
    const response = await fetch(url, options);
    const data: T = await response.json();
    return { data };
  } catch (error) {
    return { data: null as unknown as T, error: error instanceof Error ? error.message : 'Unknown error' };
  }
}

export function isUser(obj: any): obj is User {
  return obj && typeof obj.id === 'number' && typeof obj.name === 'string' && typeof obj.email === 'string';
}
