import React from 'react';
import ReactDOM from 'react-dom/client';
import { BrowserRouter } from 'react-router-dom';
import App from './App';
import './index.css';

const rootElement = document.getElementById('root');
if (!rootElement) throw new Error('Failed to find the root element');

const root = ReactDOM.createRoot(rootElement);
root.render(
  <React.StrictMode>
    <BrowserRouter>
      <App />
    </BrowserRouter>
  </React.StrictMode>
);

import React from 'react';
import { Routes, Route } from 'react-router-dom';
import Header from './components/Header';
import Footer from './components/Footer';
import Home from './pages/Home';
import Menu from './pages/Menu';
import Reservation from './pages/Reservation';
import ChefProfile from './pages/ChefProfile';
import DishGallery from './pages/DishGallery';

const App: React.FC = () => {
  return (
    <div className="min-h-screen flex flex-col bg-orange-50">
      <Header restaurantName="Café Delight" tagline="Delicious food, warm atmosphere" />
      <main className="flex-1">
        <Routes>
          <Route path="/" element={<Home />} />
          <Route path="/menu" element={<Menu />} />
          <Route path="/reservation" element={<Reservation availableSlots={['12:00 PM', '1:00 PM', '2:00 PM', '6:00 PM', '7:00 PM', '8:00 PM']} />} />
          <Route path="/chef-profile" element={<ChefProfile name="Chef Maria" photo="https://images.unsplash.com/photo-1540189549336-e6e99c3679fe?w=1200" biography="A passionate chef with 15 years of experience" specialties={['Italian Cuisine', 'Mediterranean']} philosophy="Fresh ingredients make the best dishes" awards={['Best Chef 2023']} signatureDishes={['Pasta Carbonara', 'Tiramisu']} />} />
          <Route path="/dish-gallery" element={<DishGallery />} />
        </Routes>
      </main>
      <Footer restaurantName="Café Delight" address="123 Main St, City" phone="+1 234 567 890" socialLinks={[{ platform: 'Facebook', url: '#' }, { platform: 'Instagram', url: '#' }]} />
    </div>
  );
};

export default App;

@tailwind base;
@tailwind components;
@tailwind utilities;

@layer base {
  html {
    font-size: 16px;
    line-height: 1.5;
    color: #3a3a3a;
    background-color: #fffaf0;
  }
  body {
    font-family: 'Inter', sans-serif;
    margin: 0;
    padding: 0;
  }
  h1, h2, h3, h4, h5, h6 {
    margin: 0;
    font-weight: 600;
  }
  p {
    margin: 0;
  }
}

@layer components {
  .btn {
    @apply px-4 py-2 rounded-lg text-white bg-orange-600 hover:bg-orange-500 transition duration-300 ease-in-out;
  }
  .card {
    @apply bg-white shadow-md rounded-lg p-6 mb-4;
  }
  .header {
    @apply bg-orange-600 text-white p-4;
  }
  .footer {
    @apply bg-gray-200 text-gray-700 p-4 text-center;
  }
}

@layer utilities {
  .text-warm-orange {
    color: #ff6f20;
  }
  .bg-warm-orange {
    background-color: #ff6f20;
  }
  @screen sm {
    .container {
      @apply max-w-sm mx-auto;
    }
  }
  @screen md {
    .container {
      @apply max-w-md mx-auto;
    }
  }
  @screen lg {
    .container {
      @apply max-w-lg mx-auto;
    }
  }
  @screen xl {
    .container {
      @apply max-w-xl mx-auto;
    }
  }
}

import React, { useState } from 'react';
import { Link } from 'react-router-dom';

interface MenuItem {
  id: number;
  name: string;
  description: string;
  price: string;
  imageUrl: string;
  dietaryInfo: string[];
}

const menuItems: MenuItem[] = [
  {
    id: 1,
    name: 'Bruschetta',
    description: 'Grilled bread topped with tomatoes, garlic, and basil.',
    price: '$8.00',
    imageUrl: 'https://images.unsplash.com/photo-1572442388796-11668a67e53d?w=400',
    dietaryInfo: ['vegetarian'],
  },
  {
    id: 2,
    name: 'Grilled Salmon',
    description: 'Fresh salmon fillet grilled to perfection.',
    price: '$18.00',
    imageUrl: 'https://images.unsplash.com/photo-1467003909585-2f8a72700288?w=400',
    dietaryInfo: ['gluten-free'],
  },
  {
    id: 3,
    name: 'Chocolate Lava Cake',
    description: 'Rich chocolate cake with a gooey center.',
    price: '$7.00',
    imageUrl: 'https://images.unsplash.com/photo-1563805042-7684c019e1cb?w=400',
    dietaryInfo: ['vegetarian'],
  },
  {
    id: 4,
    name: 'Mojito',
    description: 'Refreshing cocktail with mint and lime.',
    price: '$10.00',
    imageUrl: 'https://images.unsplash.com/photo-1556679343-c7306c1976bc?w=400',
    dietaryInfo: [],
  },
];

const Menu: React.FC = () => {
  const [searchTerm, setSearchTerm] = useState('');

  const filteredItems = menuItems.filter(item =>
    item.name.toLowerCase().includes(searchTerm.toLowerCase())
  );

  return (
    <div className="min-h-screen p-8 bg-gray-100">
      <div className="max-w-4xl mx-auto">
        <h1 className="text-4xl font-bold mb-6 text-center">Menu</h1>
        <input
          type="text"
          placeholder="Search menu items..."
          className="mb-6 p-2 border border-gray-300 rounded w-full"
          value={searchTerm}
          onChange={e => setSearchTerm(e.target.value)}
        />
        <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-3 gap-6">
          {filteredItems.map(item => (
            <div key={item.id} className="bg-white p-4 rounded shadow-md">
              <img
                src={item.imageUrl}
                alt={item.name}
                className="w-full h-48 object-cover rounded"
              />
              <h2 className="text-xl font-semibold mt-4">{item.name}</h2>
              <p className="text-gray-600">{item.description}</p>
              <p className="font-bold text-lg mt-2">{item.price}</p>
              <p className="text-sm text-gray-500 mt-1">
                {item.dietaryInfo.length > 0 ? `Dietary Info: ${item.dietaryInfo.join(', ')}` : 'No dietary info'}
              </p>
            </div>
          ))}
        </div>
        <Link to="/" className="block mt-8 text-blue-500 hover:underline text-center">
          Back to Home
        </Link>
      </div>
    </div>
  );
};

export default Menu;

import React, { useState } from 'react';
import { Link } from 'react-router-dom';

interface ReservationFormProps {
  availableSlots: string[];
}

const Reservation: React.FC<ReservationFormProps> = ({ availableSlots }) => {
  const [date, setDate] = useState<string>('');
  const [time, setTime] = useState<string>('');
  const [guests, setGuests] = useState<number>(1);
  const [table, setTable] = useState<string>('');
  const [specialRequests, setSpecialRequests] = useState<string>('');
  const [bookingStatus, setBookingStatus] = useState<string>('');

  const handleReservation = (e: React.FormEvent) => {
    e.preventDefault();
    setBookingStatus(`Reservation confirmed for ${guests} guests on ${date} at ${time}`);
  };

  return (
    <div className="min-h-screen p-8 bg-orange-100">
      <div className="max-w-4xl mx-auto bg-white p-6 rounded-lg shadow-md">
        <h1 className="text-4xl font-bold mb-6 text-orange-600">Make a Reservation</h1>
        <form onSubmit={handleReservation} className="space-y-4">
          <input
            type="date"
            value={date}
            onChange={(e) => setDate(e.target.value)}
            className="w-full p-2 border border-gray-300 rounded"
            required
          />
          <select
            value={time}
            onChange={(e) => setTime(e.target.value)}
            className="w-full p-2 border border-gray-300 rounded"
            required
          >
            <option value="" disabled>Select Time</option>
            {availableSlots.map((slot, index) => (
              <option key={index} value={slot}>{slot}</option>
            ))}
          </select>
          <input
            type="number"
            value={guests}
            onChange={(e) => setGuests(Number(e.target.value))}
            min={1}
            className="w-full p-2 border border-gray-300 rounded"
            required
          />
          <input
            type="text"
            value={table}
            onChange={(e) => setTable(e.target.value)}
            placeholder="Preferred Table"
            className="w-full p-2 border border-gray-300 rounded"
          />
          <textarea
            value={specialRequests}
            onChange={(e) => setSpecialRequests(e.target.value)}
            placeholder="Special Requests"
            className="w-full p-2 border border-gray-300 rounded"
          />
          <button
            type="submit"
            className="w-full p-2 bg-orange-600 text-white rounded hover:bg-orange-500"
          >
            Confirm Reservation
          </button>
        </form>
        {bookingStatus && <p className="mt-4 text-green-600">{bookingStatus}</p>}
        <Link to="/" className="mt-4 inline-block text-blue-500 hover:underline">
          Back to Home
        </Link>
      </div>
    </div>
  );
};

export default Reservation;

import React from 'react';
import { Link } from 'react-router-dom';

interface ChefProfileProps {
  name: string;
  photo: string;
  biography: string;
  specialties: string[];
  philosophy: string;
  awards: string[];
  signatureDishes: string[];
}

const ChefProfile: React.FC<ChefProfileProps> = ({
  name,
  photo,
  biography,
  specialties,
  philosophy,
  awards,
  signatureDishes,
}) => {
  return (
    <div className="min-h-screen p-8 bg-orange-100">
      <div className="max-w-4xl mx-auto bg-white rounded-lg shadow-md p-6">
        <h1 className="text-4xl font-bold text-orange-600 mb-4">{name}</h1>
        <img
          src={photo}
          alt={`${name}'s profile`}
          className="w-full h-64 object-cover rounded-lg mb-4"
        />
        <p className="text-gray-700 mb-4">{biography}</p>
        <h2 className="text-2xl font-semibold text-orange-500 mb-2">Specialties</h2>
        <ul className="list-disc list-inside mb-4">
          {specialties.map((specialty, index) => (
            <li key={index} className="text-gray-600">{specialty}</li>
          ))}
        </ul>
        <h2 className="text-2xl font-semibold text-orange-500 mb-2">Cooking Philosophy</h2>
        <p className="text-gray-700 mb-4">{philosophy}</p>
        <h2 className="text-2xl font-semibold text-orange-500 mb-2">Awards & Recognition</h2>
        <ul className="list-disc list-inside mb-4">
          {awards.map((award, index) => (
            <li key={index} className="text-gray-600">{award}</li>
          ))}
        </ul>
        <h2 className="text-2xl font-semibold text-orange-500 mb-2">Signature Dishes</h2>
        <ul className="list-disc list-inside mb-4">
          {signatureDishes.map((dish, index) => (
            <li key={index} className="text-gray-600">{dish}</li>
          ))}
        </ul>
        <Link to="/" className="text-blue-500 hover:underline">
          Back to Home
        </Link>
      </div>
    </div>
  );
};

export default ChefProfile;

